INSERT INTO "yeast" VALUES(128,'WLP300 - Hefeweizen Ale Yeast','Wheat','Liquid',0.035,'false','White Labs','300',20.0,22.0,'Low',74.0,'This famous German yeast is a strain used in the production of traditional, authentic wheat beers. It produces the banana and clove nose traditionally associated with German wheat beers and leaves the desired cloudy look of traditional German wheat beers.','',0,10,'false',-1,-1,0,0);
INSERT INTO "yeast" VALUES(129,'WLP565 - Belgian Saison I Yeast','Ale','Liquid',0.035,'false','White Labs','565',20.0,24.0,'Medium',70.0,'Classic Saison yeast from Wallonia. It produces earthy, peppery, and spicy notes. Slightly sweet. With high gravity Saisons, brewers may wish to dry the beer with an alternate yeast added after 75% fermentation.','',0,10,'false',-1,-1,0,0);
INSERT INTO "yeast" VALUES(130,'WLP011 - European Ale Yeast','Ale','Liquid',0.035,'false','White Labs','011',18.0,21.0,'Medium',67.0,'Malty, Northern European-origin ale yeast. Low ester production, giving a clean profile. Little to no sulfur production. Low attenuation helps to contribute to the malty character. Good for Alt, Kolsch, malty English ales, and fruit beers.','',0,10,'false',-1,-1,0,0);
INSERT INTO "yeast" VALUES(131,'WLP630 - Berliner Weisse Blend','Ale','Liquid',0.0350002722816,'false','White Labs','630',20.0,22.0,'Medium',75.0,'A blend of traditional German Weizen yeast and Lactobacillus to create a subtle, tart, drinkable beer. Can take several months to develop tart character. Perfect for traditional Berliner Weisse.','',0,10,'false',-1,-1,0,1);
